import React, { useState } from 'react';
import php from '../pictures/php_emblem.png';
import python from '../pictures/python-logo.png';
import db from '../pictures/database-icon.png';

function Skills() {
    const [expandedSkill, setExpandedSkill] = useState(null);

    const toggleExpand = (skill) => {
        setExpandedSkill(expandedSkill === skill ? null : skill);
    };

    return(
        <div className="w-full relative bg-cover bg-center bg-gray-700 p-5" >
            <h2 className="text-2xl font-bold text-white">Dovednosti</h2>
            <div className="grid grid-cols-3 gap-4 mt-4">
                <div 
                    className="flex flex-col items-center cursor-pointer hover:bg-gray-600 p-4 rounded-lg transition duration-300" 
                    onClick={() => toggleExpand('php')}
                >
                    <img src={php} alt="Skill 1" className="w-24 h-24"/>
                    <p className="text-white mt-2">Skill 1</p>
                    {expandedSkill === 'php' && (
                        <div className="text-white mt-2">
                            <p>Další informace o PHP...</p>
                        </div>
                    )}
                </div>
                <div 
                    className="flex flex-col items-center cursor-pointer hover:bg-gray-600 p-4 rounded-lg transition duration-300" 
                    onClick={() => toggleExpand('python')}
                >
                    <img src={python} alt="Skill 2" className="w-24 h-24"/>
                    <p className="text-white mt-2">Skill 2</p>
                    {expandedSkill === 'python' && (
                        <div className="text-white mt-2">
                            <p>Další informace o Pythonu...</p>
                        </div>
                    )}
                </div>
                <div 
                    className="flex flex-col items-center cursor-pointer hover:bg-gray-600 p-4 rounded-lg transition duration-300" 
                    onClick={() => toggleExpand('db')}
                >
                    <img src={db} alt="Skill 3" className="w-24 h-24"/>
                    <p className="text-white mt-2">Skill 3</p>
                    {expandedSkill === 'db' && (
                        <div className="text-white mt-2">
                            <p>Další informace o databázích...</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}
export default Skills;
